document.addEventListener("DOMContentLoaded",function(){const n=[{id:"wave-sloth",src:"https://images.gigigatgat.ca/wave-sloth.gif",alt:"Wave sloth"},{id:"flying-avocado",src:"https://images.gigigatgat.ca/avocado-flying.gif",alt:"Flying avocado"}],o=document.getElementById("gif-container");if(!o)return;const i=parseInt(localStorage.getItem("lastGifIndex"))||-1;let e;do e=Math.floor(Math.random()*n.length);while(n.length>1&&e===i)localStorage.setItem("lastGifIndex",e);const s=n[e],t=document.createElement("img");t.id=s.id,t.src=s.src,t.alt=s.alt,o.appendChild(t)})